changeSet.sql:
CREATE TABLE ArtifactGroup ( name varchar(255) NOT NULL, commonName varchar(255) DEFAULT NULL, description varchar(255) DEFAULT NULL, createdOn timestamp NOT NULL, createdBy varchar(255) NOT NULL, PRIMARY KEY (name) );
CREATE TABLE ArtifactProduct ( name varchar(255) NOT NULL, commonName varchar(255) DEFAULT NULL, description varchar(255) DEFAULT NULL, group_name varchar(255) DEFAULT NULL, PRIMARY KEY (name), FOREIGN KEY (group_name) REFERENCES ArtifactGroup(name) );
CREATE TABLE ArtifactVersion ( name varchar(255) NOT NULL, createdAt timestamp DEFAULT NULL, artifact_name varchar(255) DEFAULT NULL, PRIMARY KEY (name), FOREIGN KEY (artifact_name) REFERENCES ArtifactProduct(name) );
CREATE TABLE AsyncQuery ( id varchar(36) not null, createdOn timestamp, principalName varchar(255), query varchar(255), queryType integer, requestId varchar(36), status varchar(255), updatedOn timestamp, contentLength integer, responseBody text, completedOn timestamp, httpStatus integer, recordCount integer, primary key (id) );
CREATE TABLE TableExport ( id varchar(36) not null, createdOn timestamp, principalName varchar(255), query varchar(255), queryType integer, requestId varchar(36), resultType varchar(255), status varchar(255), updatedOn timestamp, contentLength integer, responseBody text, completedOn timestamp, httpStatus integer, recordCount integer, message text, url varchar(255), primary key (id) );
CREATE TABLE Downloads ( group_id varchar(255) NOT NULL, product_id varchar(255) NOT NULL, date timestamp NOT NULL, downloads integer NOT NULL, FOREIGN KEY (group_id) REFERENCES ArtifactGroup(name), FOREIGN KEY (product_id) REFERENCES ArtifactProduct(name) );

